import classes from './checkout.module.css';
import { useRef,useState } from 'react';

const Checkout = (props) => {

  const [formValidation, setformValidation] = useState({
    name:true,
    street:true,
    postal:true,
    city:true,
  })
  const isEmpty = value => value.trim() === '';
  const isSixchar = value => value.trim().length !== 5;

  const nameRef = useRef();
  const streetRef = useRef();
  const postalcodeRef = useRef();
  const cityRef = useRef();

  const entered_Name = nameRef.current.value
  const entered_Street = nameRef.current.value
  const entered_City = nameRef.current.value
  const entered_N = nameRef.current.value

  const enteredNameValid = !isEmpty(nameRef);
  const enteredStreetValid = !isEmpty(streetRef);
  const enteredCityeValid = !isEmpty(cityRef);
  const enteredPostalCodeValid = !isSixchar(postalcodeRef);

  const formValid = enteredNameValid && enteredStreetValid && enteredCityeValid && enteredPostalCodeValid;

  setformValidation({
    name:enteredNameValid,
    postal:enteredPostalCodeValid,
    street:enteredStreetValid,
    city:enteredCityeValid,
  })

  if(!formValid){
    return;
  }

  const confirmHandler = (event) => {
    event.preventDefault();
  };

  

  return (
    <form className={classes.form} onSubmit={confirmHandler}>
      <div className={classes.control}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' ref={nameRef} />
        {!formValidation.name && <p>Please enter a valid name!</p>}
      </div>
      <div className={classes.control}>
        <label htmlFor='street'>Street</label>
        <input type='text' id='street' ref={streetRef} />
        {!formValidation.street && <p>Please enter a valid name!</p>}
      </div>
      <div className={classes.control}>
        <label htmlFor='postal'>Postal Code</label>
        <input type='text' id='postal' ref={postalcodeRef}/>
        {!formValidation.postal && <p>Please enter a valid name!</p>}
      </div>
      <div className={classes.control}>
        <label htmlFor='city'>City</label>
        <input type='text' id='city' ref={cityRef}/>
        {!formValidation.city && <p>Please enter a valid name!</p>}
      </div>
      <div className={classes.actions}>
        <button type='button' onClick={props.Oncancel}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
